import React from "react";
import SpeechRecognition, {
  useSpeechRecognition,
} from "react-speech-recognition";

export default function Livetrans() {
  const startListening = () =>
    SpeechRecognition.startListening({ continuous: true, language: "en-IN" });
  const { transcript, browserSupportsSpeechRecognition } =
    useSpeechRecognition();

  if (!browserSupportsSpeechRecognition) {
    return null;
  }

  return (
    <>
      <div
        className="container"
        style={{
          width: "100%",
          height: "100%",
          display: "flex",
          
        }}
      >
        <h2>Speech to Text Converter</h2>
        {/* <p>
          A React hook that converts speech from the microphone to text and
          makes it available to your React components.
        </p> */}
      </div>
      <div className="main-content">{transcript}</div>

      <div className="btn-style">
        <button  variant="success"
                className="mx-4" onClick={startListening}>Start Listening</button>
        <button onClick={SpeechRecognition.stopListening}>
          Stop Listening
        </button>
      </div>
    </>
  );
}
