import React, { useState } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import image from "../images/login.jpg";
import { NavLink } from "react-router-dom";
import { useNavigate } from "react-router-dom";

export default function Signin() {
  const history = useNavigate();
  var bg = {
    backgroundImage: `url(${image})`,
    backgroundPosition: "center",
    backgroundSize: "cover",
    backgroundRepeat: "no-repeat",
    width: "100vw",
    height: "100vh",
  };

  const [inpval, setInpval] = useState({
    email: "",
    password: "",
  });
  console.log(inpval);

  const getdata = (e) => {
    // console.log(e.target.value);
    const { value, name } = e.target;
    // console.log(value,name);
    setInpval(() => {
      return {
        ...inpval,
        [name]: value,
      };
    });
  };

  const validate = (e) => {
    e.preventDefault();
    const { email, password } = inpval;
    if (email === "") {
      alert("email field is required");
    } else if (!email.includes("@")) {
      alert("please enter a valid email address");
    } else if (password === "") {
      alert("enter a password");
    } else {
      alert("Login in sucessful");
      history("/firstpage");
    }
  };
  return (
    <>
      <div className="image" style={bg}>
        <div className="mx-4">
          <h3 className="text-center col-lg-3" style={{ color: "white" }}>
            Sign In
          </h3>
          <Form>
            <Form.Group className="mx-4 col-lg-3 my-3">
              <Form.Control
                type="email"
                name="email"
                onChange={getdata}
                placeholder="Enter your email"
              />
            </Form.Group>
            <Form.Group className="mx-4 col-lg-3 my-3">
              <Form.Control
                type="password"
                name="password"
                onChange={getdata}
                placeholder="Password"
              />
            </Form.Group>
            <Button
              variant="success"
              className="mx-4 col-lg-1"
              onClick={validate}
              type="submit"
            >
              Submit
            </Button>
          </Form>
          <p className="mt-3 mx-5" style={{ color: "white" }}>
            Don't have an account ?{" "}
            <span>
              <NavLink style={{ color: "red" }} to="/">
                Sign Up
              </NavLink>
            </span>
          </p>
        </div>
      </div>
    </>
  );
}
