import React,{useState} from 'react'

export default function TextForm(props) {
    const upper=()=>{
        let s=text.toUpperCase();
        setText(s)
    }
    const lower=()=>{
        let s=text.toLowerCase();
        setText(s)
    }
    const erase=()=>{
        let s='';
        setText(s)
    }
    const onchange=(event)=>{
        setText(event.target.value)
    }
    const[txt,setText]=useState("Enter text here...")
  return (
    <div>
        <h2>{props.title}</h2>
        <div className="mb-3">
        <textarea className="form-control" value={txt} onChange={onchange} id="box" rows="8"></textarea>
        </div>
        <button className="btn btn-primary mx-2" onclick={upper}>convert to uppercase</button>
        <button className="btn btn-primary mx-2" onclick={lower}>convert to lowercase</button>
        <button className="btn btn-primary mx-2" onclick={erase}>clear</button>
    </div>
  )
}
