import React,{useState} from 'react'
import PropTypes from 'prop-types'

export default function TextForm(props) {
    const click=()=>{
        let s=Text.toUpperCase();
        setText(s)
    }
    const click1=()=>{
        let s=Text.toLowerCase();
        setText(s)
    }
    const clea=()=>{
        let s='';
        setText(s)
    }
    const Change=(event)=>{
        setText(event.target.value)
    }
    const[Text,setText]=useState("Enter text here...")
  return (
    <div>
        <h2>{props.title}</h2>
        <div className="mb-3">
        <textarea className="form-control" value={Text} onChange={Change} id="box" rows="8"></textarea>
        </div>
        <button className="btn btn-primary mx-2" onclick={upper}>convert to uppercase</button>
        <button className="btn btn-primary mx-2" onclick={lower}>convert to lowercase</button>
        <button className="btn btn-primary mx-2" onclick={erase}>clear</button>
    </div>
  )
}
