import React, { useState } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import image from "../images/login.jpg";
import { NavLink } from "react-router-dom";
import { useNavigate } from 'react-router-dom';

export default function Login() {
  var bg = {
    backgroundImage: `url(${image})`,
    backgroundPosition: "center",
    backgroundSize: "cover",
    backgroundRepeat: "no-repeat",
  };

  const history=useNavigate();

  const [inpval, setInpval] = useState({
    name: "",
    date: "",
    email: "",
    password: "",
  });
  console.log(inpval);

  const getdata = (e) => {
    // console.log(e.target.value);
    const { value, name } = e.target;
    // console.log(value,name);
    setInpval(() => {
      return {
        ...inpval,
        [name]: value,
      };
    });
  };

  const validate = (e) => {
    e.preventDefault();
    const { name, date, email, password } = inpval;
    if (name === "") {
      alert("name field is required");
    }
    if (date === "") {
      alert("dob field is required");
    } else if (email === "") {
      alert("email field is required");
    } else if (!email.includes("@")) {
      alert("please enter a valid email address");
    } else if (password === "") {
      alert("enter a password");
    } else {
        alert("Login in sucessful");
        history("/firstpage");
    }
  };

  return (
    <>
      <div className="image" style={{...bg, width: "100%", height: "100%", display: "flex", alignItems: "center", justifyContent: "center"}}>
        <div className="mx-4" >
          <h3 className="text-center col-lg-" style={{ color: "white" }}>
            Sign Up
          </h3>
          <Form>
            <Form.Group className="mx-4 my-3">
              <Form.Control
                type="text"
                name="name"
                onChange={getdata}
                placeholder="Enter your name"
              />
            </Form.Group>
            <Form.Group className="mx-4 my-3">
              <Form.Control type="date" name="date" onChange={getdata} />
            </Form.Group>
            <Form.Group className="mx-4 my-3">
              <Form.Control
                type="email"
                name="email"
                onChange={getdata}
                placeholder="Enter your email"
              />
            </Form.Group>
            <Form.Group className="mx-4 my-3">
              <Form.Control
                type="password"
                name="password"
                onChange={getdata}
                placeholder="Password"
              />
            </Form.Group>
            <Button
              variant="success"
              className="mx-4 col-lg-1"
              onClick={validate}
              type="submit"
            >
              Submit
            </Button>
          </Form>
          <p className="mt-3 mx-5" style={{ color: "white" }}>
            Already have an account ?
            <span>
              <NavLink style={{ color: "red" }} to="/login">
                Sign In
              </NavLink>
            </span>
          </p>
        </div>
      </div>
    </>
  );
}
