import React,{useState} from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import image from '../images/login.jpg';

export default function Login() {
    var bg={
        backgroundImage: `url(${image})`,
        backgroundPosition: 'center',
        backgroundSize: 'cover',
        backgroundRepeat: 'no-repeat',
        width: '100vw',
        height:'100vh'
    }

    const[inpval,setInpval]=useState({
        name:"",
        email:"",
        password:""
    })

    const getdata=(e)=>{
        // console.log(e.target.value);
        const[value,name]=e.tar
    }

  return (
    <>
        <div className="image" style={bg}>
            <div className='mx-4'>
                <h3 className='text-center col-lg-3' style={{color:'white'}}>Sign Up</h3>
                <Form>
                    <Form.Group className='mx-4 col-lg-3 my-3'>
                        <Form.Control type='text' name='name' onChange={getdata} placeholder='Enter your name'/>
                    </Form.Group>
                    <Form.Group className='mx-4 col-lg-3 my-3'>
                        <Form.Control type='email' name='email' onChange={getdata} placeholder='Enter your email'/>
                    </Form.Group>
                    <Form.Group className='mx-4 col-lg-3 my-3'>
                        <Form.Control type='password' name='password' onChange={getdata} placeholder='Password'/>
                    </Form.Group>
                    <Button variant="success" className='mx-4 col-lg-1' type='submit'>Submit</Button>
                </Form>
                <p className='mt-3 mx-5' style={{color:'white'}}>Already have an account ? <span>Sign In</span></p>
            </div>
        </div>
    </>
  )
}
